    height: 100vh;
    //width: 100px;

    .sidebar__item {
      text-align: center;
    }

    .sidebar__item svg {
      padding: 10px;


      &:hover {
        cursor: pointer;
      }

      &.active {
        border-radius: 15px;
        box-shadow: 0 0 0px 3px black;
      }
    }
  }

  .sidebar__nav {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;

    ul {
      display: flex;
      flex-direction: column;
      padding: 30px 0;
      gap: 30px;
    }
  }


